#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Sun Oct  6 11:14:13 2019@author: xg7"""class Bucketsort:        def __init__(self, sizeBucket):        self.sizeBucket = sizeBucket        self.buckets = {}        self.data = None        def _distributeElementsIntoBuckets(self):        for i in self.data:            self.buckets.setdefault(i // 10, []).append(i)                    def sort(self, inputList):        lst = []        self.data = inputList        self._distributeElementsIntoBuckets()        for i in sorted(self.buckets.keys()):            lst.extend(sorted(self.buckets[i]))        return lst            if __name__ == "__main__":     ## main     import random            random.seed(0)        BktSort = Bucketsort(10)    print(BktSort.buckets)        listA = []    for i in range(100):        a = random.randint(0,100)        listA.append(a)    print(listA)    listB = BktSort.sort(listA)    print("SORTED:", listB)    